import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { firstValueFrom } from 'rxjs';
import { JobService } from 'src/app/models/job-service.model';
import { Job } from 'src/app/models/job.model';
import { CountingStatisticResponse } from 'src/app/response/counting-statistic-response';
import { JobsService } from 'src/app/services/job/job.service';
import { StatisticService } from 'src/app/services/statistic/statistic.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  constructor(private statisticService: StatisticService,
              private jobService: JobsService,
              private router: Router){}

              decoratedGardens ?:number;
  registeredOwners ?:number;
  registeredDecorators ?:number;
  job24Hours ?:number;
  job7Days ?:number;
  job30Days ?:number;

  async ngOnInit() {
    try {
      const jobStatistic = await firstValueFrom(this.statisticService.getByRequestTime()) as Array<CountingStatisticResponse>;
      this.job24Hours = jobStatistic[0].variableCount;
      this.job7Days = jobStatistic[1].variableCount;
      this.job30Days = jobStatistic[2].variableCount;
      const job = await firstValueFrom(this.jobService.getAll()) as Array<Job>;
      this.decoratedGardens = job.length;
    }
    catch(error){
      console.log(error);
    }
  }
}
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{decoratedGardens}}</h1>
          <p class="card-text">Gardens Decorated</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{registeredOwners}}</h1>
          <p class="card-text">Registered Owners</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{registeredDecorators }}</h1>
          <p class="card-text">Registered Decorators</p>
        </div>
      </div>
    </div>
  </div>
</div>
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job24Hours}}</h1>
          <p class="card-text">Jobs in last 24 hours</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job7Days}}</h1>
          <p class="card-text">Jobs in last 7 days</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job30Days }}</h1>
          <p class="card-text">Jobs in last 30 days</p>
        </div>
      </div>
    </div>
  </div>
</div>
