import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { firstValueFrom } from 'rxjs';
import { UserType } from 'src/app/enums/user-type.enum';
import { Company } from 'src/app/models/company.model';
import { JobService } from 'src/app/models/job-service.model';
import { Job } from 'src/app/models/job.model';
import { User } from 'src/app/models/user.model';
import { CompaniesWithWorkers } from 'src/app/response/companies-with-workers';
import { CountingStatisticResponse } from 'src/app/response/counting-statistic-response';
import { CompaniesService } from 'src/app/services/company/company.service';
import { JobsService } from 'src/app/services/job/job.service';
import { PhotoService } from 'src/app/services/photo/photo.service';
import { StatisticService } from 'src/app/services/statistic/statistic.service';
import { UserService } from 'src/app/services/user/user.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  constructor(private statisticService: StatisticService,
              private jobService: JobsService,
              private userService: UserService,
              private photoService: PhotoService,
              private companyService: CompaniesService,
              private router: Router){}

              decoratedGardens ?:number;
  registeredOwners ?:number;
  registeredDecorators ?:number;
  job24Hours ?:number;
  job7Days ?:number;
  job30Days ?:number;
  images :Array<String> = [];
  companies :Array<CompaniesWithWorkers> = [];
  viewCompanies :Array<CompaniesWithWorkers> = [];
  searchTermName: string = '';
  searchTermAddress: string = '';

  async ngOnInit() {
    try {
      const jobStatistic = await firstValueFrom(this.statisticService.getByRequestTime()) as Array<CountingStatisticResponse>;
      this.job24Hours = jobStatistic[0].variableCount;
      this.job7Days = jobStatistic[1].variableCount;
      this.job30Days = jobStatistic[2].variableCount;
      const job = await firstValueFrom(this.jobService.getAll()) as Array<Job>;
      this.decoratedGardens = job.length;
      const owners = await firstValueFrom(this.userService.getByUserTypeId(UserType.Owner)) as Array<User>;
      this.registeredOwners = owners.length;
      const decorators = await firstValueFrom(this.userService.getByUserTypeId(UserType.Decorator)) as Array<User>;
      this.registeredDecorators = decorators.length;
      const photos = await firstValueFrom(this.photoService.getTopK(3)) as Array<String>;
      photos.forEach(photo => {
        this.images.push("../../../../assets/photos/"+photo);
      });
      const companies = await firstValueFrom(this.companyService.getAllCompaniesWithWorkers()) as Array<CompaniesWithWorkers>;
      this.companies = companies;
      this.viewCompanies = companies;
    }
    catch(error){
      console.log(error);
    }
  }

  search(): void {
    this.viewCompanies = this.companies.filter(company => {
      const matchesName = company.name.toLowerCase().includes(this.searchTermName.toLowerCase());
      const matchesAddress = company.address.toLowerCase().includes(this.searchTermAddress.toLowerCase());
      return matchesName && matchesAddress;
    });
  }

  sortByName(): void {
    this.viewCompanies = [...this.viewCompanies].sort((a, b) => a.name.localeCompare(b.name));
  }

  sortByAddress(): void {
    this.viewCompanies = [...this.viewCompanies].sort((a, b) => a.address.localeCompare(b.address));
  }
}
<!-- STATISTIC -->
<div class="container">
  <h2>Statistic</h2>
  <div class="row">
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{decoratedGardens}}</h1>
          <p class="card-text">Gardens Decorated</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{registeredOwners}}</h1>
          <p class="card-text">Registered Owners</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{registeredDecorators }}</h1>
          <p class="card-text">Registered Decorators</p>
        </div>
      </div>
    </div>
  </div>
</div>
<div class="container">
  <div class="row">
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job24Hours}}</h1>
          <p class="card-text">Jobs in last 24 hours</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job7Days}}</h1>
          <p class="card-text">Jobs in last 7 days</p>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="card text-center">
        <div class="card-body">
          <h1 class="display-1">{{job30Days }}</h1>
          <p class="card-text">Jobs in last 30 days</p>
        </div>
      </div>
    </div>
  </div>
</div>
<!-- GALLERY -->
<div class="container">
  <h2>Gallery</h2>
  <div class="row">
    <div class="col-md-4" *ngFor="let image of images">
      <div class="card text-center">
        <img [src]="image" class="card-img-top" alt="Image">
      </div>
    </div>
  </div>
</div>
<!-- COMPANIES -->
<div class="container">
  <h2>Companies and Workers</h2>
  <div class="mb-3">
    <input type="text" class="form-control mb-2" placeholder="Search by company name" [(ngModel)]="searchTermName">
    <input type="text" class="form-control mb-2" placeholder="Search by address" [(ngModel)]="searchTermAddress">
    <button class="btn btn-primary" (click)="search()">Search</button>
    <button class="btn btn-secondary ml-2" (click)="sortByName()">Sort by Name ({{ sortDirectionName }})</button>
    <button class="btn btn-secondary ml-2" (click)="sortByAddress()">Sort by Address ({{ sortDirectionAddress }})</button>
  </div>
  <table class="table table-striped">
    <thead>
      <tr>
        <th>Company ID</th>
        <th>Company Name</th>
        <th>Address</th>
        <th>Workers</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let company of viewCompanies">
        <td>{{ company.companyId }}</td>
        <td>{{ company.name }}</td>
        <td>{{ company.address }}</td>
        <td>
          <ul>
            <li *ngFor="let worker of company.workers">
              {{ worker.name }} {{ worker.lastname }}
            </li>
          </ul>
        </td>
      </tr>
    </tbody>
  </table>
</div>
