import { Component, OnInit } from '@angular/core';
import { User } from 'src/app/models/user.model';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  user: User = new User();
  isLogged = true;
  isAdmin = false;
  isOwner = false;
  isDecorator = false;

  constructor() {}

  ngOnInit(): void {
    this.user = JSON.parse(localStorage.getItem('user') || '{}');
    if (this.user == null) {
      this.isLogged = false;
    }
    if (this.user.user_type_id == 3) {
      this.isAdmin = true;
    }
    else if (this.user.user_type_id == 1) {
      this.isOwner = true;
    }
    else if (this.user.user_type_id == 2) {
      this.isDecorator = true;
    }

  }

  logout() {
    localStorage.removeItem('user');
  }
}
<!-- header.component.html -->
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">YourApp</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNav">
    <ul class="navbar-nav">
      <!-- Common Link -->
      <li class="nav-item">
        <a class="nav-link" href="#">Home</a>
      </li>
      <!-- Dropdown Menu inside Navbar -->
      <li class="nav-item dropdown">
        <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
          Options
        </a>
        <div class="dropdown-menu" aria-labelledby="navbarDropdown">
          <a class="dropdown-item" href="#">Profile</a>
          <a class="dropdown-item" href="#">Settings</a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item" href="#" (click)="logout()">Logout</a>
        </div>
      </li>

      <!-- Admin-specific Links -->
      <li class="nav-item" *ngIf="isAdmin">
        <a class="nav-link" href="#">Admin Dashboard</a>
      </li>

      <!-- Owner-specific Links -->
      <li class="nav-item" *ngIf="isOwner">
        <a class="nav-link" href="#">Owner Profile</a>
      </li>

      <!-- Decorator-specific Links -->
      <li class="nav-item" *ngIf="isDecorator">
        <a class="nav-link" href="#">Decorator Tools</a>
      </li>
    </ul>
  </div>
</nav>
